ss="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">&times;</button>                <h4 class="modal-title">Make Ads featured</h4>            </div>            <form method="post" id="fetauredAdsData" action="<?php echo base_url('newsletter/importData') ?>" enctype="multipart/form-data">                <div class="modal-body">                    <a href="<?php echo front_base_url().'public/images/sample.xlsx' ?>" class="pull-right" download><i class="fa fa-arrow-circle-down" aria-hidden="true"></i> Download Sample</a>                        <label>Choose File:</label>                        <input type="file" class="form-control validate[required]" id="fileuplod" name="icon" onChange="check_file(this.value,'fileuplod')" />                </div>                <div class="modal-footer">                    <input type="submit" class="btn btn-success" value="Submit" />                    <button type="button" class="btn btn-warning closepopup" data-dismiss="modal">Cancel</button>                </div>            </form>        </div>    </div></div><!--Add Email Form--><div id="addemailform" class="modal fade" role="dialog">    <div class="modal-dialog">        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">&times;</button>                <h4 class="modal-title">Add Email</h4>            </div>            <form method="post" id="newsletteremailform" action="<?php echo base_url('newsletter/do_addedit') ?>" enctype="multipart/form-data">                <div class="modal-body">                                        <label>Email ID:</label>                    <input type="text" class="form-control validate[required,custom[email]]" id="email_id" name="email_id" />                </div>                <div class="modal-footer">                    <input type="submit" class="btn btn-success" value="Submit" />                    <button type="button" class="btn btn-warning closepopupaddemail" data-dismiss="modal">Cancel</button>                </div>            </form>        </div>    </div></div><!-- Modal --><div id="myModal" class="modal fade" role="dialog">    <div class="modal-dialog" style="width:800px; margin:15px auto;">        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">&times;</button>                <h4 class="modal-title">Compose Notification Message</h4>            </div>            <div class="modal-body">                <form role="form" id="notifyform" class="form-horizontal form-label-left" id="form1" action="<?php echo base_url('newsletter/sendNotification');?>" method="post" enctype="multipart/form-data" onsubmit="return validate_data()">                    <div class="form-group">                        <label>Notification Subject <span class="required">*</span></label>                        <input type="text" name="notification_subject" id="notification_subject" placeholder="Notification Subjects" class="form-control validate[required]"/>                    </div>                    <div class="form-group">                        <label>Choose Template <span class="required">*</span></label>                        <select class="form-control validate[required]" name="template_no" id="template_no">                            <option value="">Select Template</option>                            <?php foreach ($templateData as $template){?>                                <option value="<?php echo $template['id'] ?>"><?php echo $template['title'] ?></option>                            <?php }?>                        </select>                    </div>                    <input type="hidden" name="memberNeedNotify" class="memberNeedNotify" />                    <button type="submit" class="btn btn-success">Submit</button>                </form>            </div>            <div class="modal-footer">                <button type="button" class="btn btn-default notifyformPopup" data-dismiss="modal">Close</button>            </div>        </div>    </div></div><script>    $(document).ready(function(){        $("#fetauredAdsData").validationEngine();        $("#notifyform").validationEngine();        $("#newsletteremailform").validationEngine();    })    notifyMemberArr = [];    $(document).on('click','.notify_check_all',function(){        if($(this).is(":checked")){            $(".notify_member").show();            $('.individual_notify_check').attr('checked',true);            $('.individual_notify_check:checked').each(function () {                if(jQuery.inArray($(this).val(), notifyMemberArr) ==-1){                    notifyMemberArr.push($(this).val());                }            });        }else{            notifyMemberArr = [];            $(".notify_member").hide();            $('.individual_notify_check').attr('checked',false);        }        console.log(notifyMemberArr);    });    $(document).on('click','.individual_notify_check',function () {        if($(this).is(':checked')){            if(jQuery.inArray($(this).val(), notifyMemberArr) ==-1){                notifyMemberArr.push($(this).val());            }        }else{            var index = notifyMemberArr.indexOf($(this).val());            if (index > -1) {                notifyMemberArr.splice(index, 1);            }        }        if(notifyMemberArr.length>0){            $(".notify_member").show();        }else{            $(".notify_member").hide();        }        console.log(notifyMemberArr);    });    $(document).on('click','.notify_member',function(){        if(notifyMemberArr.length>0){            $(".memberNeedNotify").val(notifyMemberArr.join(','));        }else{            alert('Invalid Request');        }    });    $(document).on('click','.closepopup',function(){       $("#fetauredAdsData")[0].reset();       $(".parentFormfetauredAdsData").remove();    });    $(document).on('click','.notifyformPopup',function(){       $("#notifyform")[0].reset();       $(".notification_subjectformError").remove();       $(".template_noformError").remove();    });    $(document).on('click','.closepopupaddemail',function(){       $("#newsletteremailform")[0].reset();       $(".email_idformError").remove();       $(".nameformError").remove();    });    function check_file(value,id){        //console.log(value+" "+id);        var ext = value.split('.').pop().toLowerCase();        if($.inArray(ext, ['xlsx','csv','xlsm','xls']) == -1) {            alert('Invalid file type !');            $("#"+id).val('');        }    }</script>